import React from "react"
import { connect } from 'react-redux'
import {Line} from 'react-chartjs-2'

const LineChart = (props) => { // i just want to pass it different props 

  // let today = new Date
  // let month = ["January", "February", "March", "April", "May", "June", "July", "August", "September", "October", "November", "December"]
  // let currentMonth = month[today.getMonth()]

  const data = {
    labels: ['money in', 'money out', 'money left'],
    datasets: [
      {
        label: 'savings',
        backgroundColor: ['#B21F00', '#175000'],
        data: [0, 45, (65-45)],
        borderWidth: 0
      },
      {
        label: 'debt',
        backgroundColor: ['#B21F00'],
        data: [65],
        borderWidth: 0
      },
      {
        label: 'investments',
        backgroundColor: ['#B21F00'],
        data: [65],
        borderWidth: 0
      }
    ]
  }

  const options = {
    title:{
      display:true,
      text: "trends",
      fontSize:20
    },
    legend:{
      display:true,
      position:'right'
    },
  }

  return (
    <div>
    <Line data={data} options={options} width={100} height={100} />
    </div>
  )
}

const mapStateToProps = (state) => {
  return {
    transactions: state.linkReducer.monthTransactions
  }
}

const mapDispacthToProps = (dispatch) => {
  return {
    dispatch
  }
}

export default connect(mapStateToProps, mapDispacthToProps)(LineChart)
