import React from "react";
import {connect} from 'react-redux'
import {Link} from 'react-router-dom'


const NavBar = (props) => {

  const logout = () => { // if youre logged in, refresh, logout. doesnt refresh page. 
    localStorage.clear()
    props.resetApp()
  } 

  const handleAddUser = (event) => {
   if (!props.users[1]){
     alert( `account code: ${props.account_code}`)
   }
  }


  const handleUserView = (event) => {
    props.setFilter(event.target.id)
  }


  return (
    // https://materializecss.com/navbar.html
    // hmmm if i get rid of the a tag i lose some padding. come back later
    <div> 
      <nav className="nav-extended">
        <div className="nav-wrapper">
          <a className="brand-logo">props.cool_logo_and_app_name</a>
          <a data-target="mobile-demo" className="sidenav-trigger"><i className="material-icons">menu</i></a>
          <ul id="nav-mobile" className="right hide-on-med-and-down">
            <li><Link to="/"><a>Home</a></Link></li>
            <li><Link to="/month"><a>Month</a></Link></li>
            <li><Link to="/trends"><a>Trends</a></Link></li>
            <li onClick={logout}><a>Logout</a></li>
          </ul>
        </div>
        <div className="nav-content">
          <ul className="tabs tabs-transparent">
            <li className="tab" id="0"><a>
              {props.users[0] ? props.users[0].username :null }
            </a></li>
            <li className="tab" id="1" onClick = {handleAddUser}><a>
              {props.users[1] ? props.users[1].username : " + USER" }
            </a></li>
            <li className="tab"><a>
              {props.users[1] ? props.users[0].username + '+' + props.users[1].username : null}
            </a></li>
          </ul>
        </div>
      </nav>

      <ul className="sidenav" id="mobile-demo"> {/* this does not work */}
        <li><a>Month</a></li>
        <li><a>Trends</a></li>
        <li onClick={logout}><Link to="/"><a>Logout</a></Link></li> {/* that Link does not work */}
      </ul>
    </div>
  )

}

const mapStateToProps = (state) => {
  return { 
    users: state.authReducer.account.users,
    account_code:  state.authReducer.account.code
  }
}

const mapDispatchToProps = (dispatch) => {
  return {
    resetApp: ( () => dispatch({type: "resetApp"}) ),
    setFilter: ( (filter) => dispatch({type: "setFilter", filter: filter}))
  }
} 


export default connect(mapStateToProps, mapDispatchToProps)(NavBar);
