import React from "react"
import { connect } from 'react-redux'

const BalancePanel = (props) => {

  const calcBalance = () => {
      let accounts 
      switch(props.fiter){
        case "cash":
          // filter for checking savings
          props.accounts.fitler(account => account.subtype)
          accounts = "cash accounts"
          break
        case "investments":
          accounts = "investment accounts"
          // filter for 401k cd money market
          break
        case "debt":
          accounts = "debt accounts"
          break
        default: 
          accounts = "all acounts"
      }
      // now iterate through all accounts and add them up 
  }

  return (
    // <div className="bal"> 
    <div className="list-group">
      <div className="list-group-item list-group-item-action active">
        Balance Panel
        <div className="list-group-item list-group-item-action">
          ${calcBalance()}
        </div>
      </div>
    </div>
  )

}

const mapStateToProps = (state) => {
  return {
    accounts: state.linkReducer.accounts,
    filter: state.dashReducer.filter
  }
}

const mapDispacthToProps = (dispatch) => {
  return {
    dispatch
    // inc: (() => dispatch({type: "inc"}))
  }
}

export default connect(mapStateToProps, mapDispacthToProps)(BalancePanel)
