import React from "react"
import { connect } from 'react-redux'
import {Bar, Doughnut} from 'react-chartjs-2'

const MonthChart = (props) => { // i just want to pass it different props 

  const data = {
    // labels: ['January', 'February', 'March', 'April', 'May'],
    labels: ['mooney in', 'money out', 'money left'],

    datasets: [
      // {
      //   label: 'Rainfall',
      //   backgroundColor: [
      //     '#B21F00',
      //     '#C9DE00',
      //     '#2FDE00',
      //     '#00A6B4',
      //     '#6800B4'
      //   ],
      //   hoverBackgroundColor: [
      //   '#501800',
      //   '#4B5000',
      //   '#175000',
      //   '#003350',
      //   '#35014F'
      //   ],
      //   data: [65, 59, 80, 81, 56]
      // },
      {
        label: 'outcome',
        backgroundColor: ['#B21F00', '#175000'],
        data: [0, 45, (65-45)]
      },
      {
        label: 'income',
        backgroundColor: ['#B21F00'],
        data: [65]
      }
    ]
  }


  const options = {
    title:{
      display:true,
      text:'April',
      fontSize:20
    },
    legend:{
      display:true,
      position:'right'
    },
    elements: { 
      arc: {
      rotation: 1 * Math.PI,
      circumfrence: 1 * Math.PI
    }}

  }


  return (
    <div>
    <Doughnut data={data} options={options}/>
    </div>
  )
}

const mapStateToProps = (state) => {
  return {
    transactions: state.linkReducer.monthTransactions
  }
}

const mapDispacthToProps = (dispatch) => {
  return {
    dispatch
    // inc: (() => dispatc({type: "inc"}))
  }
}

export default connect(mapStateToProps, mapDispacthToProps)(MonthChart)
