const initialState = {
  savings: [2000, 2200, 2400, 2600, 2600, 2650],
  debt: [10000, 9500, 9000, 8500, 8000, 7500],
  investments: []
}

export default function trendReducer (state=initialState, action){
  switch(action.type){
    case 'adjustTrends':
      let m
      return{ 
        ...state,
        mSaving: m,
        mDebt: m,
        mOverall: m
      }
    case "resetTrend":
      return {
        initialState
      }
    default: {
      return state
    }
  }
}
