import React from "react"
import { connect } from 'react-redux'



const AccountsPanel = (props) => {

  const showAccount = (account) => {
    return(
      <div className="list-group-item list-group-item-action" key={account.account_id} id={account.account_id} onClick={handleAccountFilter}>
        <div>{account.name}</div>
        <span className="badge">${account.balances.current}</span>
        <div>subtype: {account.subtype}</div>
        <div>type: {account.type}</div>
        {/* <div>account_id: {account.account_id}</div> */}
        <div> {account.user.username}</div>
        {/* institution */}
      </div>
    )
  }

  const handleTypeFilter = (event) => {
    props.setTypeView(event.target.id)
  }

  const handleAccountFilter = (event) => {
    debugger
    props.setAccountView(event.target.id)
  }



  return (
    <div className="acc" > 
    <div className="list-group">
      <div className="list-group-item list-group-item-action active">
        Accounts Panel
        <div> allow for a toggle here btwn cash investments and debt</div>
        <div> this would update balances/transactions/accounts</div>
        <button id="available" onClick={handleTypeFilter}> avilable funds </button>
        <button id="investments" onClick={handleTypeFilter}> investments </button>
        <button id="debt" onClick={handleTypeFilter}> debt </button>
        {props.accounts.map( account => showAccount(account))}
      </div>
    </div>
    </div >
  )

}

const mapStateToProps = (state) => {
  return {
    accounts: state.linkReducer.accounts
  }
}

const mapDispacthToProps = (dispatch) => {
  return {
    setTypeView: ((filter) => dispatch({type: "setTypeView", filter: filter}))
  }
}

export default connect(mapStateToProps, mapDispacthToProps)(AccountsPanel)
