const initialState = {
  transactions: [],
  accounts: [],
  monthTransactions: [],

  transactionsDisplay: [],
  accountsDisplay: [],

  userView: null,
  typeView: null,
  accountView: null
}

export default function linkReducer(state=initialState, action){
  switch(action.type){

    case 'storeData': // action.data = {transacton: [{trans}, {trans} ... ]}, accounts: [{acc}, {acc} ...]}
    // would be nice to sort transactions by date
      return{
        ...state,
        transactions: action.data.transactions,
        accounts: action.data.accounts,
      }

    case 'addData':
      return {
        ...state,
        transactions: [...state.transactions, ...action.data.transactions],
        accounts: [...state.accounts, ...action.data.accounts],
      }

    case 'storeMonth':
      return {
        ...state,
        monthTransactions: action.transactions.transactions
      }


    // I WOULD LIKE TO HAVE A SEPARATE VIEWCREDUCER
    // BUT IDK HOW TO DEAL WITH GETTIING TRANSACTIONS AND ACCOUNTS FROM HERE INTO THERE

    case 'setUserView': // filter trans and accounts for bal, trans, acc panels
      debugger 
      return {
        ...state,
        userView: action.username,
      }

    case "setTypeView": // filter trans and acc for balance, trans and acc panels
      return {
        ...state,
        typeView: action.filter
      }

    case "setAccountView": // filter just trans for trans panel
      let accountView
      if (action.filter === state.accountView){accountView = null}
      return {
        ...state,
        // accountView: account_id
        // accountView: action.filter
        accountView: accountView

      }

    case "resetApp":
      return initialState;

    default: {
      return state
    }
  }



}
