import React, { useEffect } from "react"
import { connect } from 'react-redux'
import UsersAccounts from '../components/UsersAccounts'

const Settings = (props) => {

  const banks = () => { 
    let bankAccounts = []
    debugger
    props.accounts.reduce((arr, account) => {
      console.log("arr", arr)
      console.log("account", account)
      if (arr.includes(account.item_id)){
        arr.push(account.item_id)
        bankAccounts.push({name: account.institution, id: account.item_id})
      }
      return arr
    }, [])
    console.log("banks", bankAccounts)
  }


  return (
    <div>
      <h3>Here show users and their accounts</h3>
      <h3>option to delete users</h3>
      <h3>option to delete institutions</h3>
      {/* {props.users.map(user => user ? <UsersAccounts user={user} banks={banks()} /> : null )} */}
      {banks()}
    </div>
  )
}

const mapStateToProps = (state) => {
  return {
    users : state.authReducer.account.users,
    accounts: state.linkReducer.accounts,
  }
}

const mapDispacthToProps = (dispatch) => {
  return {
    dispatch
  }
}

export default connect(mapStateToProps, mapDispacthToProps)(Settings)
