  const showUser = (user) => {
    return (
      <div>
        {/* <h1>{user.username}:</h1> */}
        <h1> linked banks: </h1>
        {showAccounts(user)}
      </div>
    )
  }

  const showAccounts = (user) => {
    console.log("accounts", props.accounts)
    if (!props.accounts.length){
      return <p> no accounts linked </p>
   } else {
     let distinctAccounts = [ ... new Set(props.accounts.map( account => account.item_id)) ] 
      // return <p> show accounts</p>
      return  distinctAccounts.map(account => <li>{account.name}</li>) 

   }
  }

  console.log(props)
  return (
    <div>
      <h3>Here show users and their accounts</h3>
      <h3>option to delete users</h3>
      <h3>option to delete institutions</h3>
      {props.users.map(user => user ? <UsersAccounts /> : null )}
    </div>
  )
}

const mapStateToProps = (state) => {
  return {
    users : state.authReducer.account.users,
    accounts: state.linkReducer.accounts
  }
}

const mapDispacthToProps = (dispatch) => {
  return {
    dispatch
  }
}

export default connect(mapStateToProps, mapDispacthToProps)(Settings)
