import React from "react"
import { connect } from 'react-redux'

const AdjustTrends = (props) => {

  const increaseTrend = (event) => {
    let amount
    switch(event.target.id){
      case "mSaving":
        amount = props.mSaving + 20
        props.adjustSavings(amount)
        break
      case "mDebt":
        amount = props.mDebt + 20
        props.adjustDebt(amount)
        break 
    }
    // props.adjustTrends()
    console.log("+", amount)
  }

  const decreaseTrend = (event) => {
    let amount
    switch(event.target.id){
      case "mSaving":
        amount = props.mSaving - 20
        props.adjustSavings(amount)

        break
      case "mDebt":
        amount = props.mDebt - 20
        props.adjustDebt(amount)
        break 
    }
    // props.adjustTrends()
    console.log("+", amount)
  }

  return (
    <div> 
      <div class="btn-group" role="group" >
        <div>
          <h5 style={{display: "inline"}}> Save: ${props.mSaving} </h5>
          <button type="button" class="btn btn-secondary" id="mSaving" onClick={increaseTrend}>+</button>
          <button type="button" class="btn btn-secondary" id="mSaving" onClick={decreaseTrend}>-</button>
        </div>

        <div> 
          <h5 style={{display: "inline"}}> Reduce Debt: ${-props.mDebt} </h5>
          <button type="button" class="btn btn-secondary" id="mDebt" onClick={increaseTrend}>+</button>
          <button type="button" class="btn btn-secondary" id="mDebt" onClick={decreaseTrend}>-</button>
        </div>

      </div>
    </div>
  )

}

const mapStateToProps = (state) => {
  return {
    mSaving: state.trendReducer.mSaving,
    mDebt: state.trendReducer.mDebt
  }
}

const mapDispacthToProps = (dispatch) => {
  return {
    adjustSavings: ( (mSavings) => dispatch({type: "adjustTrends", mSavings: mSavings})),
    adjustDebt: ( (mDebt) => dispatch({type: "adjustDebt", mDebt: mDebt}))

  }
}

export default connect(mapStateToProps, mapDispacthToProps)(AdjustTrends)
