const initialState = {
  viewUser: null,
  transactionsDisplay: null,
  accountsDisplay: null
}

export default function viewReducer(state=initialState, action){
  switch(action.type){
    case 'setUserVew':
      let username
      switch (action.data.user){
        case "user1":
          username = state.accounts.users[0].username
          break 
        case 'user2': 
          username = state.accounts.users[1].username
          break
        default: // if it is both, userView is null
          username = null
      }

      if (username){
        filteredTransactions = state.transactions.filter( transaction => transaction)
      }


      return {
        ...state,
        viewUser: username,
        transactionsDisplay: action.data.transactions,
        accountsDisplay: action.data.accounts
      }

    case "resetApp":
      return initialState;
    default: {
      return state
    }
  }
}
