import React from "react"
import AccountsPanel from '../components/AccountsPanel'
import TransactionsPanel from '../components/TransactionPanel'
import BalancePanel from '../components/BalancePanel'
import {connect} from 'react-redux'
import { usePromiseTracker } from "react-promise-tracker";
import PreLoader from "../components/PreLoader"
import NoAccounts from "../components/NoAccounts"



const Dash = (props) => {

  const { promiseInProgress } = usePromiseTracker(); // will return t or f. tracking promise in app useEffect 
  return (
    promiseInProgress 
    ? <PreLoader/>
    : props.accounts.length
      ? (
        <div className="dash-grid-container">
          <div className="dash-balance">
            <BalancePanel /> 
          </div>
          <div className="dash-accounts">
            <button type="button" className="btn btn-primary btn-lg btn-block" style={{color: "white", backgroundColor: "black"}}> accounts </button>
            {/* <h5>accounts</h5> */}
            <AccountsPanel /> 
          </div>
          <div className="dash-transactions">
          <button type="button" className="btn btn-primary btn-lg btn-block" style={{color: "white", backgroundColor: "black"}}> transactions </button>
            <TransactionsPanel transactions={props.transactions} vh={55}/> 
          </div>
        </div>
      )
      : <NoAccounts />

  )
}

const mapStateToProps = (state) => {
  return { 
    transactions: state.linkReducer.transactionsDisplay,
    accounts: state.linkReducer.accounts
  }
}

const mapDispatchToProps = (dispatch) => {
  return {
    dispatch
  }
} 

export default connect(mapStateToProps, mapDispatchToProps)(Dash);
