import React from "react"
import { connect } from 'react-redux'

const BalancePanel = (props) => {

  const calcBalance = () => {
      let balance = props.accounts.reduce( (acc, i) => { 
        debugger

        if(acc.type === "credit" || acc.type === "loan" ){
          return (acc - i.balances.current)
        } else if (acc.type === "depository" || acc.type === "investments"){ 
          return (acc + i.balances.current) 
        } else {
          console.log("unexpected account type", acc)
          return (acc + i.balances.current) 
        }
      }, 0)
      return balance
  }

  return (
    <div className="pan"> 
    <div className="list-group">
      <div className="list-group-item list-group-item-action active">
        Balance Panel
        <div className="list-group-item list-group-item-action">
          ${calcBalance()}
        </div>
      </div>
    </div>
    </div>
  )

}

const mapStateToProps = (state) => {
  return {
    accounts: state.linkReducer.accounts,
  }
}

const mapDispacthToProps = (dispatch) => {
  return {
    // inc: (() => dispatch({type: "inc"}))
  }
}

export default connect(mapStateToProps, mapDispacthToProps)(BalancePanel)
