const initialState = {
  viewUser: null,
  transactionsDisplay: null,
  accountsDisplay: null
}

export default function viewReducer(state=initialState, action){
  switch(action.type){
    case 'setUserVew':
      let user
      switch (action.data.user){
        case "user1":
          user = state.accounts.users[0]
          break 
        case 'user2': 
          user = state.accounts.users[1]
          break
        default: // if it is both, userView is null
          user = null
      }

      return {
        ...state,
        viewUser: action.data.user,
        transactionsDisplay: action.data.transactions,
        accountsDisplay: action.data.accounts
      }

    case "resetApp":
      return initialState;
    default: {
      return state
    }
  }
}
