const initialState = {
  user: localStorage.user_id ? {user_id: localStorage.user_id} : null ,
  account_id: null,
}

export default function authReducer(state=initialState, action){
  // debugger
  switch(action.type){
    case 'setCurrentUser':
      return{ ...state,
        user: {
          // username: action.user.username, 
          // user_id: action.user.user_id,
          // users: action.user.users
          user: action.user,
          account: action.user.account
        },
        // account_id: action.user.account_id
      }
    case "resetApp":
      return initialState;
    default: {
      return state
    }
  }
}
