import React from "react"
import { connect } from 'react-redux'

const UserAccounts = (props) => {

  const showAccounts = (username) => {
    let banksIds = [ ... new Set (props.accounts.map(account => account.item_id))] // these are accounts with distinct bank ids
    let usersBanks = banksIds.filter(account => account.user.username == username) // these are accounts with distinct bank ids for one user
    return usersBanks.map(bank => 
      <li className="list-group-item">{bank.institution}</li>
    )
  }


  return (
    <div className="card" style={{"width": "18rem"}}>
      <div className="card-header"> {props.user.username} </div>
      <ul className="list-group list-group-flush">
        {showAccounts(props.user.username)}
        <li className="list-group-item">Linked Bank</li>
        <li className="list-group-item">Linked Bank</li>
        <li className="list-group-item">Linked Bank</li>
      </ul>
    </div>
  )

}

const mapStateToProps = (state) => {
  return {
    accounts: state.linkReducer.accounts,
  }
}

const mapDispacthToProps = (dispatch) => {
  return {
    dispatch
  }
}

export default connect(mapStateToProps, mapDispacthToProps)(UserAccounts)
