const initialState = {
  transactions: [],
  accounts: [],
  monthTransactions: [],

  transactionsDisplay: [],
  accountsDisplay: [],

  userView: null
}

export default function linkReducer(state=initialState, action){

  const handleDisplay = (t, a) => {
    let displayT = t
    let displayA = a
    if (state.userView){
      displayT = state.transactions.filter(trans => trans.user.username === state.userView)
    } else {
      displayT = state.transactions
    }
    return{transactions: displayT, accounts: displayA}
  }

  switch(action.type){
    case 'storeData': // action.data = {transacton: [{trans}, {trans} ... ]}, accounts: [{acc}, {acc} ...]}
    // would be nice to sort transactions by date
    let t = action.data.transactions
    let a = action.data.accounts
    let display = handleDisplay(t, a)
    debugger
      // return{
      //   ...state,
      //   transactions: action.data.transactions,
      //   accounts: action.data.accounts,
      //   transactionsDisplay: handleDisplay().transactions,
      //   accountsDisplay: handleDisplay().accounts

      // }
      return{
        ...state,
        transactions: t,
        accounts: a,
        transactionsDisplay: display.transactions,
        accountsDisplay: display.accounts

      }
    case 'addData':
      return {
        ...state,
        transactions: [...state.transactions, ...action.data.transactions],
        accounts: [...state.accounts, ...action.data.accounts],
        transactionsDisplay: handleDisplay().transactions, // i dont think this will work right
        accountsDisplay: handleDisplay().accounts
      }
    case 'storeMonth':
      return {
        ...state,
        monthTransactions: action.transactions.transactions
      }
    case 'setUserVew':
      debugger // why doesnt this get hit 
      let username
      switch (action.filter){
        case "user1":
          username = state.accounts.users[0].username
          break 
        case 'user2': 
          username = state.accounts.users[1].username
          break
        default: // if it is both, userView is null
          username = null
      }
      return {
        ...state,
        userView: username,
        transactionsDisplay: handleDisplay().transactions,
        accountsDisplay: handleDisplay().accounts
      }
    case "resetApp":
      return initialState;
    default: {
      return state
    }
  }



}
