import React from 'react'

const style = {
  position: "fixed",
  top: "50%",
  left: "50%",

  transform: "translate(-50%, -50%)",
  textAlign: "center",
  width: "80%",

  textTransform: "uppercase",
  letterSpacing: "0.2em",

  color: "#3E3E3E",
  fontSize: "50px"
}

class ErrorBoundary extends React.Component {
  constructor(props) {
    super(props);
    this.state = { hasError: false };
  }

  static getDerivedStateFromError(error) {
    return { hasError: true }
  }

  componentDidCatch(error, errorInfo) {
    console.log(error)
    console.log(errorInfo)
  }



  render() {
    if (this.state.hasError) {
      return(
        <div>
          <div className="nav-wrapper" style={{"background": "black", height: "117px"}}>
              <a className="brand-logo" className="title" style={{padding: "0px 12px", fontSize: "30px", color: "white"}}>MONEYMOON</a>
          </div>
            <h1 style={style}>Something went wrong, please refresh the page.
            <i class="large material-icons" onClick={() => window.location.reload()}>refresh</i>
          </h1>

        </div>
      )
    }

    return this.props.children; 
  }
}

export default ErrorBoundary
