import React from "react"
import { connect } from 'react-redux'
import { Doughnut, Pie } from 'react-chartjs-2'

const MonthChart = (props) => { // i just want to pass it different props 
  console.log(props.transactions)
  let today = new Date
  let month = ["January", "February", "March", "April", "May", "June", "July", "August", "September", "October", "November", "December"]
  let currentMonth = month[today.getMonth()]


  let moneyIn = props.transactions.reduce( (acc, i) => {
    if (i.amount < 0 ){ //  "transaction with a negative amount represents money flowing into the account"
      return (-i.amount + acc)
    } else { 
      return acc
    }
  }, 0)

  let moneyOut = props.transactions.reduce ( (acc, i) => {
    if(i.amount > 0){
      return(acc + i.amount)
    } else { 
      return acc
    }
  }, 0)

  //  how to represent money out > money in

  const data = {
    labels: ['money in', 'money out', 'money left'],
    datasets: [
      {
        label: 'out',
        backgroundColor: ['#B21F00', '#175000'],
        data: [null, moneyOut, moneyIn-moneyOut], // what if that net is negative. weird it still graphs it like its positive 
        borderWidth: 0
      },
      {
        label: 'in',
        backgroundColor: ['#B21F00'],
        data: [moneyIn],
        borderWidth: 0
      }
    ]
  }

  const options = {
    title:{
      display:true,
      text: currentMonth,
      fontSize:20
    },
    legend:{
      display:true,
      position:'right'
    },
    rotation: 1 * Math.PI, 
    circumference: 1 * Math.PI ,
    maintainAspectRatio: false
  }


  let eachCategory = props.transactions.map(transaction => transaction.category[0])
  let distinctCategories = [...new Set(eachCategory)]

  const piedata = {
    labels: distinctCategories,
    datasets: [
      {
        label: 'out',
        backgroundColor: ['#d8ea7c', '#175000', '#124000', '#175024', '#275000', '#175100', '#175040', '#185000', '#186000', '#171100', '#745000', '#199000'],
        data: [100, 1000, 200], // what if that net is negative. weird it still graphs it like its positive 
        borderWidth: 0
      },
    ]
  }

  const pieoptions = {
    title:{
      display:true,
      text: currentMonth,
      fontSize:20
    },
    legend:{
      display:true,
      position:'right'
    },
    rotation: 1 * Math.PI, 
    circumference: 2 * Math.PI ,
    maintainAspectRatio: false
  }

  return (
    <div>
    {/* idk why they both get huge if i show both */}
    {/* <Doughnut data={data} options={options} width={300} height={300} /> */}
    <Pie data={piedata} options={pieoptions} width={300} height={300} />
    </div>
  )
}

const mapStateToProps = (state) => {
  return {
    transactions: state.linkReducer.monthTransactions
  }
}

const mapDispacthToProps = (dispatch) => {
  return {
    dispatch
    // inc: (() => dispatc({type: "inc"}))
  }
}

export default connect(mapStateToProps, mapDispacthToProps)(MonthChart)
