import React, { Component } from "react";
import { PlaidLink }from "react-plaid-link";
import {connect} from 'react-redux'

class Link extends Component {

  handleOnSuccess(public_token, metadata) {
    // fetch("http://localhost:8000/auth/public_token", {
    fetch("http://localhost:3000/get_access_token", {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
        // this is going to need auth
      },
      body: JSON.stringify({
        public_token: public_token,
        user_id: localStorage.user_id // shouldnt this come from store
      })
    })
    .then(resp => resp.json())
    .then(response => {
      console.log("sweet now you got a access token")
      console.log(response)
      // response has transactions and accounts
    })
  }

  handleOnExit() {
    // handle the case when your user exits Link
  }

  // handleClickTransactions(res) {
  //   fetch("http://localhost:3000/transactions",{
  //     headers: {Authorization: `Bearer ${localStorage.token}`}
  //   })
  //   // fetch("http://localhost:8000/transactions")
  //   .then(resp => resp.json())
  //   .then(transactions => {
  //     // this.props.storeTransactions(transactions) // Unhandled Rejection (TypeError): Cannot read property 'props' of undefined
  //     console.log(transactions)
  //   })
  // }

  // handleClickAccounts(res){
  //   fetch("http://localhost:3000/accounts", {
  //     headers: {Authorization: `Bearer ${localStorage.token}`}
  //   })
  //   // fetch("http://localhost:8000/accounts")
  //   .then(resp => resp.json())
  //   .then(accounts => {
  //     debugger
  //     console.log(accounts)
  //   })
  // }



  render() {
    return (
      <div>
        <PlaidLink
          clientName="front-end"
          // env="development"
          env="sandbox"
          product={["transactions"]}
          // product={["auth", "transactions"]} what ddoes this actually do 
          publicKey="38e9fa8478f20a384db53c1176e9b7"
          onExit={this.handleOnExit}
          onSuccess={this.handleOnSuccess}
          className="test"
        >
          Open Link and connect your bank!
        </PlaidLink>
        <div>
          {/* <button onClick={this.handleClickTransactions}>Get Transactions</button> */}
          {/* <button onClick={this.handleClickAccounts}>Get Accounts</button> */}
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return { 
    state
  }
}

const mapDispatchToProps = (dispatch) => {
  return{
    storeTransactions: ( (transactions) => dispatch({type: "storeTransactions", transactions: transactions}) )
  }
} 

export default connect(mapStateToProps, mapDispatchToProps)(Link);
