const initialState = {
  user: null,
  account: null,
}

export default function authReducer(state=initialState, action){
  switch(action.type){
    case 'setCurrentUser':
      return{
        ...state,
        user: {username: action.username, id: action.id},
        // account_id: account.id
      }
    case 'clearCurrentUser':
      return{
        ...state,
        user: null,
        account: null
      }
    default: {
      return state
    }
  }
}
