import React, { Component } from "react";
import { PlaidLink }from "react-plaid-link";
import {connect} from 'react-redux'

class Link extends Component {

  handleOnSuccess(public_token, metadata) {
    fetch("http://localhost:3000/get_access_token", {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
        // this is going to need auth
      },
      body: JSON.stringify({
        public_token: public_token,
        user_id: localStorage.user_id // shouldnt this come from store. no store will go blank on page refresh 
      })
    })
    .then(resp => resp.json())
    .then(response => {
      console.log("sweet now you got a access token")
      console.log(response)

      // response has transactions and accounts
      // i need to send transactions and accoounts to store

      this.props.storeTransactions(response) // this props is undefined here
      // that means my mapToDispatch isnt working

      // localStorage.transactions = response.transactions // localStorage is not defined
      // localStrorage.accounts = response.accoounts
    })
  }

  render() {
    console.log("Link", this.props) // store transactions is here?!
    return (
      <div>
        <PlaidLink
          clientName="front-end"
          env="sandbox" // "development"
          product={["transactions"]} // product={["auth", "transactions"]} what ddoes this actually do 
          publicKey="38e9fa8478f20a384db53c1176e9b7"
          onExit={this.handleOnExit}
          onSuccess={this.handleOnSuccess}
          className="test">
          Open Link and connect your bank!
        </PlaidLink>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return { 
    state
  }
}

const mapDispatchToProps = (dispatch) => {
  return {
    storeTransactions: ( (transactions) => dispatch({type: "storeTransactions", transactions: transactions}) )
  }
} 

export default connect(mapStateToProps, mapDispatchToProps)(Link);
