const initialState = {
  savings: [],
  debt: [],
  investments: []
}

export default function trendReducer (state=initialState, action){
  switch(action.type){
    case 'adjustTrends':
      let m
      return{ 
        ...state,
        mSaving: m,
        mDebt: m,
        mOverall: m
      }
    case "resetTrend":
      return {
        initialState
      }
    default: {
      return state
    }
  }
}
