const initialState = {
  transactions: [],
  accounts: [],
  monthTransactions: [],
  transactionsDisplay: [],
  accountsDisplay: [],
  viewUser: null
}

export default function linkReducer(state=initialState, action){

  switch(action.type){
    case 'storeData': // action.data = {transacton: [{trans}, {trans} ... ]}, accounts: [{acc}, {acc} ...]}
      return{
        ...state,
        transactions: action.data.transactions,
        transactionsDisplay: action.data.transactions,
        accounts: action.data.accounts,
        accountsDisplay: action.data.accounts,
      }
    case 'addData':
      return {
        ...state,
        transactions: [...state.transactions, ...action.data.transactions],
        transactionsDisplay: [...state.transactions, ...action.data.transactions],
        accounts: [...state.accounts, ...action.data.accounts],
        accountsDisplay: [...state.accounts, ...action.data.accounts],
      }
    case 'storeMonth':
      return {
        ...state,
        monthTransactions: action.transactions.transactions
      }
    case 'setUserVew':
      debugger // why doesnt this get hit 
      let username
      switch (action.filter){
        case "user1":
          username = state.accounts.users[0].username
          break 
        case 'user2': 
          username = state.accounts.users[1].username
          break
        default: // if it is both, userView is null
          username = null
      }
      // let filteredAccounts
      // let filteredTransactions
      // if (username){
      //   filteredTransactions = state.transactions.filter( transaction => transaction.user.username === username)
      //   filteredAccounts = state.accounts.filter( account => account.user.username === username)
      // }
      // debugger
      return {
        ...state,
        viewUser: username,
        // transactionsDisplay: filteredTransactions,
        // accountsDisplay: filteredAccounts
      }
    case "resetApp":
      return initialState;
    default: {
      return state
    }
  }
}
