const initialState = {
  transactions: [],
  accounts: []
}

export default function linkReducer(state=initialState, action){
  debugger
  switch(action.type){
    case 'storeTransactions':
      return{
        ...state,
        transactions: action.transactions
      }
    case 'storeAccounts':
      return{
        ...state,
        transactions: action.accounts
      }
    default: {
      return state
    }
  }
}
