}

const gauge =  { gridArea: "gauge" }

const pie = { gridArea: "pie" }

const transactions = { gridArea: "transactions" }


const Month = (props) => {
  return (
    props.accounts.length
    ? <div>
        <div style={container}> 
          {/* <div style={left}> */}
          <div style={gauge}>

            <MonthGauge />
          </div>
          {/* <div style={right}> */}
          <div style={pie}>
            <MonthPie  />
          </div>
        </div>
        <div style ={tranactions}>
          <TransactionPanel transactions={props.transactions}/>
        </div>
      </div>
    : <NoAccounts />
  )
}

const mapStateToProps = (state) => {
  return {
    transactions: state.linkReducer.monthTransactions,
    accounts: state.linkReducer.accounts
  }
}

export default connect(mapStateToProps)(Month);
