import React from "react"
import { connect } from 'react-redux'
import { Doughnut, Pie } from 'react-chartjs-2'

const MonthChart = (props) => { // i just want to pass it different props 
  // console.log(props.transactions)
  let today = new Date
  let month = ["January", "February", "March", "April", "May", "June", "July", "August", "September", "October", "November", "December"]
  let currentMonth = month[today.getMonth()]


  //  how to represent money out > money in
  // GAUGE CHART 

  let moneyIn = props.transactions.reduce( (acc, i) => {
    if (i.amount < 0 ){ //  "transaction with a negative amount represents money flowing into the account"
      return (-i.amount + acc)
    } else { 
      return acc
    }
  }, 0)

  let moneyOut = props.transactions.reduce ( (acc, i) => {
    if(i.amount > 0){
      return(acc + i.amount)
    } else { 
      return acc
    }
  }, 0)

  let moneyLeft = moneyIn - moneyOut
  let saved = ((moneyIn - moneyOut) / moneyIn) < 0 ? (moneyIn - moneyOut) / moneyIn : 0
  let values = [moneyIn, moneyOut, moneyLeft]

  const norm = (arr) => {   // arr = [in, out, left]
    let min = arr.slice().sort((a, b) => a < b ? -1 : 1).shift()
    let positive = arr.map(x => x - min)
    let max = positive.slice().sort((a, b) => a < b ? -1 : 1).pop()
    let norm = positive.map(x => x/max)
    norm = [...norm, norm[1]-norm[2]] // [in, out, left, netNeg]
    console.log("norm", norm)
    return norm
  }

  const data = {
    labels: ['money in', 'money out', 'money left', 'money negative'],
    datasets: [
      {
        label: 'out',
        backgroundColor: ['#B21F00', '#175000'],
        // data: [null, moneyOut, moneyILeft], 
        data: [null, norm(values)[1], norm(values)[2]],
        borderWidth: 0
      },
      {
        label: 'in',
        backgroundColor: ['#B21F00'],
        // data: [null, moneyOut, moneyILeft, moneyNeg], 
        data: [norm(values)[0], null, null, norm(values)[3]],
        borderWidth: 0
      }
    ]
  }

  const options = {
    title:{
      display:true,
      text: currentMonth,
      fontSize:20
    },
    legend:{
      display:true,
      position:'right'
    },
    rotation: 1 * Math.PI, 
    circumference: 1 * Math.PI ,
    maintainAspectRatio: false
  }

  // CATEGORY CHART
  // would be cool if click each category, it filters trans by that category
  let eachCategory = props.transactions.map(transaction => transaction.category[0])
  let distinctCategories = [...new Set(eachCategory)]
  let amounts = distinctCategories.map(category => 
    props.transactions.reduce((acc, i) => {
      if(i.category[0] === category){
        return (acc + i.amount)
      } else { 
        return acc
      }
    }, 0)
  )

  const piedata = {
    labels: distinctCategories,
    datasets: [
      {
        label: 'out',
        backgroundColor: ['#d8ea7c', '#93172a', '#2f00a8', '#3cef07', '#97d817', '#5aefcf', '#af09fd', '#751b74', '#3e6764', '#631a7c', '#af5e55', '#c2443d'],
        data: amounts,
        borderWidth: 0
      },
    ]
  }

  const pieoptions = {
    title:{
      display:true,
      text: currentMonth,
      fontSize:20
    },
    legend:{
      display:true,
      position:'right'
    },
    rotation: 1 * Math.PI, 
    circumference: 2 * Math.PI ,
    maintainAspectRatio: false
  }

  return (
    <div>
      {/* <div style={{"float": "top"}}> */}
        <div>
          <h6>money in: ${moneyIn}</h6>
          <h6>money out: ${moneyOut}</h6>
          <h6>percent saved: {saved}</h6>
        </div>
        <Doughnut data={data} options={options} width={300} height={300} />
      {/* </div> */}
      {/* <div style={{"float": "bottom"}}> */}
        {/* <Pie data={piedata} options={pieoptions} width={200} height={100} /> */}
      {/* </div> */}
    </div>
  )
}

const mapStateToProps = (state) => {
  return {
    transactions: state.linkReducer.monthTransactions
  }
}

const mapDispacthToProps = (dispatch) => {
  return {
    dispatch
    // inc: (() => dispatc({type: "inc"}))
  }
}

export default connect(mapStateToProps, mapDispacthToProps)(MonthChart)
