import React from "react"
import { connect } from 'react-redux'
import { Doughnut } from 'react-chartjs-2'

const BalanceDonut = (props) => { // i just want to pass it different props




  const data = {
    labels: ["depository", "investments", "debt"],
    datasets: [
      {
        label: 'out',
        backgroundColor: ['#d8ea7c', '#93172a', '#2f00a8', '#3cef07', '#97d817', '#5aefcf', '#af09fd', '#751b74', '#3e6764', '#631a7c', '#af5e55', '#c2443d'],
        data: [1, 2, 1],
        data: calcBalance(),
        borderWidth: 0
      },
    ]
  }

  const options = {
    title:{
      display: false,
      fontSize:20
    },
    legend:{
      display:false,
      position:'right'
    },
    rotation: 1 * Math.PI, 
    circumference: 2 * Math.PI ,
    maintainAspectRatio: false
  }


  return (
      <div>
         <Doughnut  data={data} options={options} width={200} height={360} />
      </div>
  )
}

const mapStateToProps = (state) => {
  return {
    accounts: state.linkReducer.accounts
  }
}

const mapDispacthToProps = (dispatch) => {
  return {
    dispatch
  }
}

export default connect(mapStateToProps, mapDispacthToProps)(BalanceDonut)
